import React from 'react';
import { Link } from 'react-router-dom';
import './Register.css';
import headerLogo from '../../images/logo.svg';

function Register({ onRegister }) {
    const nameRef = React.useRef();
    const emailRef = React.useRef();
    const passwordRef = React.useRef();

    function handleSubmit(e) {
        e.preventDefault();
        onRegister(nameRef.current.value, emailRef.current.value, passwordRef.current.value);
    }

    return (
        <section className="popup">
            <div className="popup__content">
                <img src={headerLogo} alt="Логотип" className="popup__logo" />
                <h2 className="popup__title">Добро пожаловать!</h2>
                <form id="login-form" className="popup__form" name="login-form" onSubmit={handleSubmit}>
                    <div className="popup__info">
                        <label className="popup__label">Имя</label>
                        <input id="name" ref={nameRef} className="popup__input" name="name" type="text" placeholder="Имя" required />
                        <label className="popup__label">E-mail</label>
                        <input id="email" ref={emailRef} className="popup__input" name="login" type="text" placeholder="Email" required />
                        <label className="popup__label">Пароль</label>
                        <input id="password" ref={passwordRef} className="popup__input popup__input_password" name="password" type="password" required />
                    </div>
                    <div className="popup__button">
                        <button className="popup__save" aria-label="Кнопка входа" type="submit">Зарегистрироваться</button>
                        <div className="popup__span">
                            <span className="popup__text">Уже зарегистрированы?</span>
                            <Link to="/signin" aria-label="Кнопка входа">
                                <span className="popup__enter">Войти</span>
                            </Link>
                        </div>
                    </div>
                </form>
            </div>
        </section>
    )
}

export default Register;
