import React from 'react';
import { Link } from 'react-router-dom';
import './Profile.css';
import Header from '../Header/Header';

function Profile({ onSignOut }) {
    const nameRef = React.useRef();
    const emailRef = React.useRef();

    return (
        <>
            <Header loggedIn={true} />
            <section className="profile">
                <div className="profile__content">
                    <h2 className="profile__title">Привет, Виталий!</h2>
                    <form id="login-form" className="profile__form" name="login-form" noValidate>
                        <div className="profile__data">
                            <div className="profile__info">
                                <label className="profile__label">Имя</label>
                                <input id="name" className="profile__input" name="name" type="text" placeholder="Имя" required pattern="^[a-zA-Zа-яА-Я\s\-]*$" />
                            </div>
                            <hr className="profile__line"></hr>
                            <div className="profile__info">
                                <label className="profile__label">E-mail</label>
                                <input id="email" ref={emailRef} className="profile__input" name="email" type="email" placeholder="Email" required pattern="^[a-zA-Zа-яА-Я\s\-]*$" />
                            </div>
                        </div>
                        <div className="profile__button">
                            <button className="profile__save" aria-label="Кнопка редактировать" type="submit">Редактировать</button>
                            <Link to="/" className="profile__exit" aria-label="Кнопка выхода" onClick={onSignOut}>Выйти из аккаунта</Link>
                        </div>
                    </form>
                </div>
            </section>
        </>
    )
}

export default Profile;
