import React from 'react';
import { Link } from 'react-router-dom';
import { useFormWithValidation } from '../../utils/Hooks';
import './Profile.css';
import Header from '../Header/Header';

function Profile({ onProfileUpdate, onSignOut }) {
    const nameRef = React.useRef();
    const emailRef = React.useRef();

    const { handleChange, errors, isValid } = useFormWithValidation();

    function handleSubmit(e) {
        e.preventDefault();
        profileUpdate(nameRef.current.value, emailRef.current.value);
    }

    return (
        <>
            <Header loggedIn={true} />
            <section className="profile">
                <div className="profile__content">
                    {/* <h2 className="profile__title">Привет, {nameRef.current.value}!</h2> */}
                    <h2 className="profile__title">Привет, Виталий!</h2>
                    <form id="login-form" className="profile__form" name="login-form" onSubmit={handleSubmit} noValidate>
                        <div className="profile__data">
                            <div className="profile__info">
                                <label className="profile__label">Имя</label>
                                <input id="name" ref={nameRef} className="profile__input" name="name" type="text" onChange={handleChange} required pattern="^[a-zA-Zа-яА-Я\s\-]*$" />
                            </div>
                            <hr className="profile__line"></hr>
                            <div className="profile__info">
                                <label className="profile__label">E-mail</label>
                                <input id="email" ref={emailRef} className="profile__input" name="email" type="email" onChange={handleChange} required />
                            </div>
                        </div>
                        {/* должна быть только 1 общая ошибка */}
                        {!isValid &&
                            <div className="profile__error">{Object.values(errors).map(err => <p>{err}</p>)}
                            </div>
                        }
                        <div className="profile__button">
                            <button className={"profile__save" + (isValid ? " profile__save_active" : "")} aria-label="Кнопка редактировать" type="submit" disabled={!isValid}>Редактировать</button>
                            <Link to="/" className="profile__exit" aria-label="Кнопка выхода" onClick={onSignOut}>Выйти из аккаунта</Link>
                        </div>
                    </form>
                </div>
            </section>
        </>
    )
}

export default Profile;
